INSERT INTO mails(authorId, subject, text, dateMail) VALUES (3,'Скасовуємо штрафні санкції за кредитами','Анатолію Павловичу! Країна зараз бореться з поширенням коронавірусу – більшість громадян залишається вдома. Банк із розумінням ставиться до ситуації, тому на період з 01.03 до 31.07.2020 скасовує нарахування штрафних санкцій, пені та подвійних процентів у разі відсутності або часткового внесення обов’язкового платежу.','2016-08-07 17:40:52.756');
