INSERT INTO mails(authorId, subject, text, dateMail) VALUES (1,'Исследуйте мир, не выходя из дома','Как Вы знаете, сейчас весь мир переживает непростые времена. Мы понимаем, что в такой период особенно важно поддерживать друг друга и оставаться на связи. Чтобы помочь Вам в этом, мы будем делиться с Вами различным контентом: от информативного до развлекательного. Следите за новостями и берегите себя и своих близких. Команда программы "Местные эксперты"','2020-05-07 18:05:52.756');
